import React from "react";
import { Link } from "react-router-dom";
import { useState } from "react";

function Phone({ id, setDetails}) {
    const [phone, setPhone] = useState("");

    const getPhone = (id) => {
        axios
        .get(`${API_URL}/api/phones/${id}`)
        .then((response) => setPhone(response.data))
        .catch((err) => console.log(err))
      }

    useEffect(() => {
     getPhone
    }, [])


  return (
    <div>
      <p>{phone.description}</p>
      <p>Technical details:</p>
      <ul>
        <li>Color: {phone.color}</li>
        <li>Screen: {phone.screen}</li>
        <li>Processor: {phone.processor}</li>
        <li>Ram:{phone.ram} </li>
      </ul>
      <Link onClick={() => setDetails(false)}>Hide</Link>
    </div>
  );
}

export default Phone;
