import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";
import Phone from "../components/Phone";
import { Link } from "react-router-dom";


const API_URL = "http://localhost:5005";

function HomePage() {
  const [phones, setPhones] = useState([]);
  const [details, setDetails] = useState(false);
  const [phone, setPhone] = useState("");

  const getPhones = () => {
    axios
      .get(`${API_URL}/api/phones`)
      .then((response) => setPhones(response.data))
      .catch((err) => console.log(err));
  };

  const getPhone = (id) => {
    axios
    .get(`${API_URL}/api/phones/${id}`)
    .then((response) => setPhone(response.data))
    .catch((err) => console.log(err))
  }

  useEffect(() => {
    getPhones();
  }, []);

  return (
    <div>
      HomePage
      {phones && (
        <div>
          {phones.map((onePhone) => {
            return (
              <div key={onePhone.id}>    
                <Link onClick={() => setDetails(true)}>{onePhone.name}</Link>
                {details && <Phone phone={phone} setDetails={setDetails}/>}
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}

export default HomePage;
