import React, { useEffect, useState } from "react";
import PhoneCard from "../components/PhoneCard";
import * as phoneService from "../services/phone-services";

function PhoneListScreen() {
  const [phones, setPhones] = useState(null);

  useEffect(() => {
    phoneService.getPhones().then((phones) => setPhones(phones));
  }, []);

  return (
    <div className="container">
      <div className="input-group justify-content-center mt-3">
        <div className="form-outline">
          <input
            type="text"
            className="form-control me-2"
            placeholder="Search by origin address"
          />
        </div>
        <span
          className="input-group-text ms-1"
          style={{ backgroundColor: "white", border: "none" }}
        >
          <i className="fa fa-search"></i>
        </span>
      </div>
      {phones?.map((phone) => (
        <div className="col" hey={phone.id}>
          <PhoneCard {...phone} />
        </div>
      ))}
    </div>
  );
}

export default PhoneListScreen;
